[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<!-- -->"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set por = frappe.db.get_list('Payment Order Reference',filters={'parent':doc.name}, fields=['payment_request']) %}
    <div data-fieldname="items" data-fieldtype="Table">
        <table class="table table-bordered table-condensed">
            <thead>
                <tr>
                    <th class="" data-fieldname="items" data-fieldtype="Table">Amount</th>
                    <th class="" data-fieldname="items" data-fieldtype="Table">Bank Name</th>
                    <th class="" data-fieldname="items" data-fieldtype="Table">Beneficiary Account Number</th>
                    <th class="" data-fieldname="items" data-fieldtype="Table">Beneficiary Name</th>
                    <th class="" data-fieldname="items" data-fieldtype="Table">Reference 1</th>
                    <th class="" data-fieldname="items" data-fieldtype="Table">Reference 2</th>
                    <th class="" data-fieldname="items" data-fieldtype="Table">Beneficiary Email</th>
                    <th class="" data-fieldname="items" data-fieldtype="Table">Beneficiary Number</th>
                </tr>
            </thead>
            <tbody>
                {% for i in por %}
                    {% set pr = frappe.db.get_list('Payment Request',filters={'name':i.payment_request},fields=['grand_total','bank_account','party','reference_name']) %}
                    {% set mobile_no,bank,iban,grower_name,period = ['','','','',''] %}
                    {% set _link = frappe.get_list('Dynamic Link', filters={'link_doctype':'Supplier','parenttype':'Contact','link_name':pr[0].party},fields=['parent']) %}
                    {% if (_link is defined) and _link %}
                    {% set mobile_no = frappe.db.get_value('Contact',_link[0].parent, 'mobile_no') %}
                    {% endif %}
                    {% if pr[0].bank_account %}
                    {% set bank,iban = frappe.db.get_value('Bank Account',pr[0].bank_account,['bank','iban']) %}
                    {% set grower_name = frappe.db.get_value('Supplier',pr[0].party,'supplier_name') %}
					  {% set father_name = frappe.db.get_value('Supplier',pr[0].party,'father_name') %}
                    {% endif %}
                    {% if pr[0].reference_name %}
                    {% set period = frappe.db.get_value('Purchase Invoice',pr[0].reference_name,'posting_date') %}
                    {% endif %}
                    <tr>
                        <td class="" data-fieldname="items" data-fieldtype="Table">
                            <div class="value">{{pr[0].grand_total}}</div>
                        </td>
                        <td class="" data-fieldname="items" data-fieldtype="Table">
                            <div class="value">{{bank}}</div>
                        </td>
                        <td class="" data-fieldname="items" data-fieldtype="Table">
                            <div class="value">{{iban}}</div>
                        </td>
                        <td class="" data-fieldname="items" data-fieldtype="Table">
                            <div class="value">{{grower_name}} S/O  {{father_name}} </div>
                        </td>
                        <td class="" data-fieldname="items" data-fieldtype="Table">
                            <div class="value">Cane Payment</div>
                        </td>
                        <td class="" data-fieldname="items" data-fieldtype="Table">
                            <div class="value">{{period}}</div>
                        </td>
                        <td class="" data-fieldname="items" data-fieldtype="Table">
                            <div class="value">muhammad.aamir@gourmetpakistan.com</div>
                        </td>
                        <td class="" data-fieldname="items" data-fieldtype="Table">
                            <div class="value">
                                {% if mobile_no != '' %}
                            	    {{mobile_no}}
                            	{% endif %}

                            </div>
                        </td>
                    </tr>
                {% endfor %}
            </tbody>
        </table>
    </div>"}]
